import RNHTMLtoPDF from 'react-native-html-to-pdf';
import {woAPI} from '../api/woApi';
import moment from 'moment';
import {Platform} from 'react-native';

export const createPDFWODetails = async (id: string) => {
  const {woDetailsData, woFilesData} = (await woAPI.getWODetails(id)).data
    ?.payload;

  const htmlContent = `
	<!DOCTYPE html>
 <html lang="en">
	 <head>
		 <meta charset="UTF-8" />
		 <meta name="viewport" content="width=device-width, initial-scale=1.0" />
		 <title>Document</title>
	 </head>
	 <style>
		 @font-face {
			 font-family: 'PoppinsMedium';
			 src: local('PoppinsMedium'), url('../fonts/Poppins/Poppins-Medium.ttf') format('truetype');
			 font-weight: 500;
		 }
 
		 @font-face {
			 font-family: 'Poppins';
			 src: local('Poppins'), url('../fonts/Poppins/Poppins-Regular.ttf') format('truetype');
			 font-weight: 500;
		 }
 
		 @media print { 
		 * { 
		 		-webkit-print-color-adjust: exact !important; 
		 }
			div, tr {
        page-break-inside: avoid;
    		} 
			}
		 
		 * {
			 margin: 0;
			 padding: 0;
			 box-sizing: border-box;
		 }
 
		 body {
			 font-family: 'Poppins', sans-serif;
		 }
 
		 .content {
			 max-width: 595px;
			 margin: 0 auto;
			 padding: 26px 30px;
		 }
 
		 .icon-container {
			 display: flex;
			 align-items: center;
			 justify-content: center;
			 width: 100%;
			 padding-bottom: 25px;
		 }
 
		 .content__title {
			 color: rgb(32, 37, 52);
			 font-family: Poppins, serif;
			 font-size: 20px;
			 font-style: normal;
			 font-weight: 400;
			 line-height: 24px;
		 }
 			.content__subTitle {
			  color: rgb(32, 37, 52);
        font-family: Poppins,serif;
        font-size: 12px;
      	font-style: normal;
        font-weight: 400;
        line-height: 12px;
				margin-top: 10px;
				margin-bottom: 5px;
		 }
		 .content__title-description {
			 color: #DC3545;
			 font-family: Poppins, serif;
			 font-size: 11px;
			 font-style: normal;
			 font-weight: 500;
			 line-height: 15px;
			 padding-bottom: 10px;
		 }
 
		 .details-container {
			 display: flex;
			 flex-direction: column;
			 gap: 9px;
			 width: 100%;
		 }
 
		 .details {
			 display: flex;
			 justify-content: space-between;
			 width: 100%;
		 }
 
		 .details__title {
			 color: rgb(132, 138, 155);
			 font-family: Poppins, serif;
			 font-size: 7px;
			 font-style: normal;
			 font-weight: 500;
			 line-height: 10px;
			 text-transform: uppercase;
		 }
 
		 .details__description {
			 color: rgb(0, 0, 0);
			 font-family: Poppins, serif;
			 font-size: 8px;
			 font-style: normal;
			 font-weight: 400;
			 line-height: 11px;
		 }
 
		 .table {
			 width: 100%;
			 font-family: Poppins, serif;
			 font-size: 8px;
			 font-style: normal;
			 font-weight: 400;
			 line-height: normal;
			 background-color: rgb(221, 221, 221);
			 margin-top: 25px;
		 }
 
		 .table__title {
			 vertical-align: center;
		 }
 
		 .table__content,
		 .table__title {
			 padding: 6px 10px;
		 }
 
		 .table tbody tr td:first-child {
			 max-width: 110px;
			 min-width: 110px;
		 }
 
		 .table__title {
			 color: rgb(255, 255, 255);
			 background: #1B6BC0;
		 }
 
		 .table__content {
			 background: rgb(255, 255, 255);
		 }
 
		 .table__details-title {
			 color: rgb(32, 37, 52);
			 background: rgb(221, 221, 221);
		 }
			.content__note {
          color: rgb(32, 37, 52);
          font-family: Poppins,serif;
          font-size: 12px;
          font-style: normal;
          font-weight: 400;
          line-height: 12px;
					padding-top: 5px;
      }
      .content__notes {
          color: rgb(32, 37, 52);
          font-size: 10px;
          line-height: 10px;
      }
      .files-container {
          display: grid;
          grid-template-columns: 50% 50%;
          grid-gap: 1px;
          background-color: rgb(221, 221, 221);
          padding: 2px;
      }
      .file {
          display: flex;
          align-items: center;
          justify-content: center;
          background-color: rgb(255, 255, 255);
          padding: 8px;
          aspect-ratio: 1/1;
      }
      .file img {
          max-width: 100%;
          max-height: 100%;
      }
			.note__creator {
				color: rgb(32, 37, 52);
          font-family: Poppins,serif;
          font-size: 12px;
          font-style: normal;
          font-weight: 400;
          line-height: 12px;
			}
			.note__date {
				color: #6C757D;
          font-family: Poppins,serif;
          font-size: 10px;
          font-style: normal;
          font-weight: 400;
          line-height: 12px;
			}
			.note__container {
				border: 1px solid  rgb(221, 221, 221);
				padding: 9px 11px;
			}
				.text {
			  width: 100%;
    		height: auto;
    		padding: 8px;
				font-size: 10px;
    		box-sizing: border-box;
				word-break: break-all;
				}
	 </style>
 
	 <body>
		 <div class="content">
			 <div class="icon-container">
				 <svg width="389" height="73" viewBox="0 0 389 73" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
<rect width="389" height="73" fill="url(#pattern0_24527_143257)"/>
<defs>
<pattern id="pattern0_24527_143257" patternContentUnits="objectBoundingBox" width="1" height="1">
<use xlink:href="#image0_24527_143257" transform="matrix(0.000775457 0 0 0.00413223 -0.00172088 0)"/>
</pattern>
<image id="image0_24527_143257" width="1294" height="242" xlink:href="data:image/png;base64,"/>
</defs>
</svg>
			 </div>
			 <p class="content__title">Work Order Details</p>
			 <p class="content__title-description"># ${woDetailsData.number} - ${
    woDetailsData.title
  }</p>
			 <div class="details-container">
				 <div>
					 <div class="details">
						 <span class="details__title">prepared for</span>
						 <span class="details__title">created date</span>
					 </div>
					 <div class="details">
						 <span class="details__description">${
               woDetailsData.subcontractor?.name || '-'
             }</span>
						 <span class="details__description">${moment(woDetailsData.creationDate).format(
               'MM/DD/YYYY',
             )}</span>
					 </div>
				 </div>
 
				 <div>
					 <div class="details">
						 <span class="details__title"> wo location </span>
						 <span class="details__title"> wo supervisor(s) </span>
					 </div>
					 <div class="details">
					 <div>
						 <div class="details__description">
							 ${woDetailsData.building?.address}
						 </div>
						 <div class="details__description">
						${woDetailsData.building?.city ? woDetailsData.building?.city + ', ' : ''}${
    woDetailsData.building?.state ? woDetailsData.building?.state + ', ' : ''
  }${woDetailsData.building?.zipCode ? woDetailsData.building?.zipCode : ''}
						 </div></div>	
						 <div>

						 ${woDetailsData.supervisors
               .map(
                 supervisor =>
                   `<p class="details__description">${supervisor.firstName} ${supervisor.lastName}, ${supervisor.phone}, ${supervisor.email}</p>`,
               )
               .join('')}
						 </div>
					 </div>
				 </div>
			 </div>
 
			 <table class="table" cellpadding="0" cellspacing="1">
				 <thead>
					 <tr>
						 <td
							 colspan="2"
							 class="table__title"
						 >
							 Work Order Details
						 </td>
					 </tr>
				 </thead>
				 <tbody>
					 <tr>
						 <td class="table__content">Work Order Type</td>
						 <td class="table__content">${woDetailsData.type} ${
    woDetailsData.subtype ? ' / ' + woDetailsData.subtype : ''
  }</td>
					 </tr>
					 <tr>
						 <td class="table__content">Priority</td>
						 <td class="table__content">${woDetailsData.priority}</td>
					 </tr>
					 <tr>
						 <td class="table__content">Description</td>
						 <td class="table__content">${
               woDetailsData.description ? woDetailsData.description : '-'
             }</td>
					 </tr>
					 <tr>
						 <td class="table__content">Special Instructions</td>
						 <td class="table__content">${
               woDetailsData.specialInstructions
                 ? woDetailsData.specialInstructions
                 : '-'
             }</td>
					 </tr>
						${woDetailsData.assets
              .map(asset => {
                return `
									<tr>
										<td colspan='2' class='table__title table__details-title'>
											Asset #${asset.equipmentId} Details
										</td>
									</tr>
									<tr>
										<td class='table__content'>Name / Equipment ID</td>
										<td class='table__content'>
											${asset.name} / ${asset.equipmentId}
										</td>
									</tr>
									<tr>
										<td class='table__content'>Category / Type</td>
										<td class='table__content'>
											${asset.category.name} / ${asset.types.name}
										</td>
									</tr>
									<tr>
										<td class='table__content'>Manufacturer</td>
										<td class='table__content'>${asset.manufacturer}</td>
									</tr>
									<tr>
										<td class='table__content'>Model</td>
										<td class='table__content'>${asset.model}</td>
									</tr>
									<tr>
										<td class='table__content'>Serial Number</td>
										<td class='table__content'>${asset.serialNumber}</td>
									</tr>
								`;
              })
              .join('')}
				 </tbody>
			 </table>
			 ${
         woFilesData.files.length
           ? `<p class="content__subTitle">
        WO Files:
      </p>`
           : ''
       }
      <div class="files-container">
			${
        woFilesData.files.length > 0
          ? woFilesData.files
              .map(
                file => `
								${
                  ['jpg', 'png', 'pdf'].includes(
                    file?.split('.').at(-1).toLowerCase(),
                  )
                    ? `<div class="file"><img src=${file} alt=${file}></div>`
                    : `<p class="text">${file}</p>`
                }
        `,
              )
              .join('')
          : ''
      }
      
      </div>
    ${
      woFilesData.notes.length
        ? `<p class="content__subTitle">
        WO Notes:
      </p>`
        : ''
    }
			 ${woFilesData.notes
         .map(
           note => `
					<div class="note__container">
					<span class="note__creator">
					${note.creator.firstName} ${
             note.creator.lastName
           } <span class="note__date">${moment(note.creationDate).format(
             'MM/DD/YYYY HH:mm',
           )}</span>
					 </span>
					<p class="content__note">
       			${note.text}
     			</p>
						${
              note.noteFiles.length > 0
                ? `
						<div class="files-container" style="background-color: white;">
						${note.noteFiles
              .map(
                file => `
								${
                  ['jpg', 'png', 'pdf'].includes(
                    file?.url.split('.').at(-1).toLowerCase(),
                  )
                    ? `<div class="file"><img src=${file.url} alt=${file.url}></div>`
                    : `<p class="text">${file.url}</p>`
                }
        `,
              )
              .join('')}
						</div>
						`
                : ''
            }	
					</div>
					`,
         )
         .join('')}
    </div>
		 </div>
	 </body>
 </html>
 `;

  const fileName = `WO ${woDetailsData.number} Details`;

  try {
    let PDFOptions = {
      html: htmlContent,
      fileName,
      directory: 'Documents',
    };
    let file = await RNHTMLtoPDF.convert(PDFOptions);

    if (!file.filePath) {
      return;
    } else {
      return {
        ...file,
        type: 'application/pdf',
        uri: Platform.OS === 'ios' ? file.filePath : 'file://' + file.filePath,
        fileName,
      };
    }
  } catch (error) {
    console.log('Failed to generate pdf', error.message);
  }
};
